import React, {useState,useEffect, useMemo} from 'react';
import * as d3 from 'd3';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import AppConfig from './config';
import Header from './components/header.js';
import Footer from './components/footer.js';
import './App.css';


const App = () => (
    <body className="d-flex flex-column min-vh-100 bg-light bg-gradient">
    <div className="container-fluid p-3 bg-white" id="container">
        <Header/>
        <main>
            <div className="p-4 mb-4 bg-light rounded-3">
                <div className="container-fluid py-4">
                    <div className="row justify-content-md-center mt-4 text-center">
                        <h3 className="display-6 fw-bold text-center">
                            <i className="bi bi-collection"></i>
                            {AppConfig.projectName} ({AppConfig.projectShortName})
                        </h3>
                        <p className="col-md-12 fs-4">
                            <b>{AppConfig.projectShortName}</b> helps researchers to refine
                            HPC performance and scaling via interactive visualizations.
                        </p>
                    </div>
                </div>
            </div>
        </main>
    </div>
    <div className="container-fluid px-3">
        <div className="row">
            <div className="col-6 bg-success height-300">
                <h3 className="text-center">
                    Node-Link Panel
                </h3>
            </div>
            <div className="col-6 bg-warning height-300">
                <h3 className="text-center">
                    Line Chart Panel
                </h3>
            </div>
        </div>
        <div className="row">
            <div className="col-6 bg-danger height-300">
                <h3 className="text-center">
                    Stacked Barchart Panel
                </h3>
            </div>
            <div className="col-6 bg-info height-300">
                <h3 className="text-center">
                    Box Plots Panel
                </h3>
            </div>

        </div>
    </div>
    <Footer/>
    </body>
);


